import React, { useState, useRef, useEffect, KeyboardEvent } from 'react';
import './InputArea.css';

interface InputAreaProps {
  onSendMessage: (content: string) => void;
  isLoading: boolean;
  onStopGeneration?: () => void; // Êñ∞Â¢ûÂÅúÊ≠¢ÂáΩÊï∞
}

// Whisper API ÈÖçÁΩÆ
const WHISPER_API_URL = 'http://localhost:5000/api/transcribe';

const InputArea: React.FC<InputAreaProps> = ({ 
  onSendMessage, 
  isLoading,
  onStopGeneration 
}) => {
  const [input, setInput] = useState('');
  const [isTranscribing, setIsTranscribing] = useState(false);
  const textareaRef = useRef<HTMLTextAreaElement>(null);
  const fileInputRef = useRef<HTMLInputElement>(null);

  // Ëá™Âä®Ë∞ÉÊï¥ÊñáÊú¨ÂüüÈ´òÂ∫¶
  useEffect(() => {
    const textarea = textareaRef.current;
    if (textarea) {
      textarea.style.height = 'auto';
      const newHeight = Math.min(textarea.scrollHeight, 150);
      textarea.style.height = `${newHeight}px`;
    }
  }, [input]);

  const handleSend = () => {
    if (input.trim() && !isLoading) {
      onSendMessage(input.trim());
      setInput('');
      if (textareaRef.current) {
        textareaRef.current.style.height = 'auto';
      }
    }
  };

  const handleKeyDown = (e: KeyboardEvent<HTMLTextAreaElement>) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSend();
    }
    // EscÈîÆÂÅúÊ≠¢ÁîüÊàê
    if (e.key === 'Escape' && isLoading && onStopGeneration) {
      e.preventDefault();
      onStopGeneration();
    }
  };

  const handleClear = () => {
    setInput('');
    if (textareaRef.current) {
      textareaRef.current.style.height = 'auto';
      textareaRef.current.focus();
    }
  };

  const handleExampleClick = (example: string) => {
    setInput(example);
    if (textareaRef.current) {
      textareaRef.current.focus();
    }
  };

  // Â§ÑÁêÜÈü≥È¢ëÊñá‰ª∂‰∏ä‰º†
  const handleAudioUpload = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    // Ê£ÄÊü•Êñá‰ª∂Á±ªÂûã
    const allowedTypes = ['audio/mp3', 'audio/wav', 'audio/mp4', 'audio/m4a', 'audio/ogg', 'audio/webm'];
    const fileExtension = file.name.split('.').pop()?.toLowerCase();
    const allowedExtensions = ['mp3', 'wav', 'mp4', 'm4a', 'ogg', 'webm'];

    if (!allowedExtensions.includes(fileExtension || '')) {
      alert('‰∏çÊîØÊåÅÁöÑÈü≥È¢ëÊ†ºÂºèÔºÅÊîØÊåÅÁöÑÊ†ºÂºèÔºöMP3, WAV, MP4, M4A, OGG, WebM');
      return;
    }

    setIsTranscribing(true);

    try {
      const formData = new FormData();
      formData.append('audio', file);
      formData.append('language', 'zh'); // ÈªòËÆ§‰∏≠Êñá

      const response = await fetch(WHISPER_API_URL, {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error('Èü≥È¢ëËΩ¨ÊñáÂ≠óÂ§±Ë¥•');
      }

      const result = await response.json();

      if (result.success && result.text) {
        // Â∞ÜËΩ¨ÂΩïÊñáÊú¨Ê∑ªÂä†Âà∞ËæìÂÖ•Ê°Ü
        setInput((prev) => prev + (prev ? '\n' : '') + result.text);
        if (textareaRef.current) {
          textareaRef.current.focus();
        }
      } else {
        throw new Error('ËΩ¨ÂΩïÁªìÊûú‰∏∫Á©∫');
      }
    } catch (error) {
      console.error('Èü≥È¢ëËΩ¨ÊñáÂ≠óÈîôËØØ:', error);
      alert('Èü≥È¢ëËΩ¨ÊñáÂ≠óÂ§±Ë¥•ÔºåËØ∑Á°Æ‰øù Whisper ÊúçÂä°Â∑≤ÂêØÂä® (http://localhost:5000)');
    } finally {
      setIsTranscribing(false);
      // ÈáçÁΩÆÊñá‰ª∂ËæìÂÖ•
      if (fileInputRef.current) {
        fileInputRef.current.value = '';
      }
    }
  };

  const handleAudioButtonClick = () => {
    fileInputRef.current?.click();
  };

  return (
    <div className="input-area">
      <div className="examples">
        <span className="examples-label">ËØïËØïÈóÆÊàëÔºö</span>
        <button 
          className="example-button"
          onClick={() => handleExampleClick('Áî®JavaScriptÂÜô‰∏Ä‰∏™ÁÆÄÂçïÁöÑÂæÖÂäû‰∫ãÈ°πÂ∫îÁî®')}
          disabled={isLoading}
        >
          ‚ú® ÂÜô‰ª£Á†Å
        </button>
        <button 
          className="example-button"
          onClick={() => handleExampleClick('Ëß£Èáä‰∏Ä‰∏ãÈáèÂ≠êËÆ°ÁÆóÁöÑÂü∫Êú¨ÂéüÁêÜ')}
          disabled={isLoading}
        >
          ü§î Ëß£ÈáäÊ¶ÇÂøµ
        </button>
        <button 
          className="example-button"
          onClick={() => handleExampleClick('Â∏ÆÊàëÂà∂ÂÆö‰∏Ä‰∏™Â≠¶‰π†ReactÁöÑËÆ°Âàí')}
          disabled={isLoading}
        >
          üìö Âà∂ÂÆöËÆ°Âàí
        </button>
      </div>

      <div className="input-wrapper">
        <textarea
          ref={textareaRef}
          className="message-input"
          placeholder={isTranscribing ? 'Ê≠£Âú®ËΩ¨ÂΩïÈü≥È¢ë...' : isLoading ? 'AIÊ≠£Âú®ÊÄùËÄÉ‰∏≠... (ÊåâEscÂÅúÊ≠¢)' : 'ËæìÂÖ•Ê∂àÊÅØ... (Shift+EnterÊç¢Ë°åÔºåEnterÂèëÈÄÅ)'}
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={handleKeyDown}
          rows={1}
          disabled={isLoading || isTranscribing}
        />

        <div className="input-actions">
          {/* Èü≥È¢ë‰∏ä‰º†ÊåâÈíÆ */}
          <input
            ref={fileInputRef}
            type="file"
            accept="audio/*,.mp3,.wav,.mp4,.m4a,.ogg,.webm"
            onChange={handleAudioUpload}
            style={{ display: 'none' }}
          />
          <button 
            className="action-button audio-button"
            onClick={handleAudioButtonClick}
            disabled={isLoading || isTranscribing}
            title="‰∏ä‰º†Èü≥È¢ëËΩ¨ÊñáÂ≠ó"
          >
            {isTranscribing ? 'üîÑ' : 'üé§'}
          </button>

          <button 
            className="action-button clear-button"
            onClick={handleClear}
            disabled={!input.trim() || isLoading || isTranscribing}
            title="Ê∏ÖÁ©∫ËæìÂÖ•"
          >
            ‚úï
          </button>

          {isLoading && onStopGeneration ? (
            <button 
              className="stop-generate-button"
              onClick={onStopGeneration}
              title="ÂÅúÊ≠¢ÁîüÊàê (Esc)"
            >
              ‚èπÔ∏è ÂÅúÊ≠¢ÁîüÊàê
            </button>
          ) : (
            <button 
              className="send-button"
              onClick={handleSend}
              disabled={!input.trim() || isLoading}
            >
              {isLoading ? (
                <span className="sending">
                  <span className="spinner"></span>
                  ÂèëÈÄÅ‰∏≠...
                </span>
              ) : (
                <>
                  <span>ÂèëÈÄÅ</span>
                  <span className="send-icon">‚Üë</span>
                </>
              )}
            </button>
          )}
        </div>
      </div>

      <div className="input-hints">
        <span className="hint">üí° ÊîØÊåÅMarkdownÊ†ºÂºè</span>
        <span className="hint">üíª ÊîØÊåÅ‰ª£Á†ÅÂùó</span>
        <span className="hint">üé§ ÊîØÊåÅÈü≥È¢ëËΩ¨ÊñáÂ≠ó</span>
        <span className="hint">üìù ‰∏ä‰∏ãÊñáÈïøÂ∫¶Ôºö128K</span>
        {isLoading && (
          <span className="hint warning">‚èπÔ∏è Êåâ Esc ÂÅúÊ≠¢ÁîüÊàê</span>
        )}
        {isTranscribing && (
          <span className="hint warning">üîÑ Ê≠£Âú®ËΩ¨ÂΩïÈü≥È¢ë...</span>
        )}
      </div>
    </div>
  );
};

export default InputArea;
