import React from 'react';
import ReactMarkdown from 'react-markdown';
import remarkGfm from 'remark-gfm';
import remarkMath from 'remark-math';
import rehypeKatex from 'rehype-katex';
import 'katex/dist/katex.min.css';
import './MessageBubble.css';

interface MessageBubbleProps {
  message: {
    content: string;
    sender: 'user' | 'assistant';
    timestamp: Date;
  };
}

// ‰∏∫ code ÁªÑ‰ª∂ÂÆö‰πâÊ≠£Á°ÆÁöÑ props Á±ªÂûã
interface CodeProps {
  className?: string;
  children?: React.ReactNode;
  node?: any;
  inline?: boolean;
}

const MessageBubble: React.FC<MessageBubbleProps> = ({ message }) => {
  const isUser = message.sender === 'user';
  const timeString = message.timestamp.toLocaleTimeString([], { 
    hour: '2-digit', 
    minute: '2-digit' 
  });

  // Áî®Êà∑Ê∂àÊÅØÁõ¥Êé•ÊòæÁ§∫Ôºå‰∏çÂ§ÑÁêÜ Markdown
  if (isUser) {
    return (
      <div className="message-bubble-container user">
        <div className="message-content-wrapper">
          <div className="message-bubble user">
            <div className="message-text user-content">{message.content}</div>
            <div className="message-time">{timeString}</div>
          </div>
        </div>
        <div className="avatar user-avatar">
          <div className="avatar-icon">üë§</div>
        </div>
      </div>
    );
  }

  // AI Ê∂àÊÅØ‰ΩøÁî®ÂÆåÊï¥ÁöÑ Markdown Âíå LaTeX Ê∏≤Êüì
  return (
    <div className="message-bubble-container assistant">
      <div className="avatar ai-avatar">
        <div className="avatar-icon">ü§ñ</div>
      </div>
      <div className="message-content-wrapper">
        <div className="sender-name">AIÂä©Êâã</div>
        <div className="message-bubble assistant">
          <div className="message-text">
            <ReactMarkdown
              remarkPlugins={[remarkGfm, remarkMath]}
              rehypePlugins={[rehypeKatex]}
              components={{
                // ÈìæÊé•Â§ÑÁêÜ
                a: ({ href, children }) => (
                  <a 
                    href={href} 
                    target="_blank" 
                    rel="noopener noreferrer"
                    className="markdown-link"
                    onClick={(e) => {
                      e.stopPropagation();
                    }}
                  >
                    {children}
                  </a>
                ),

                // ‰ª£Á†ÅÂ§ÑÁêÜ - ‰ΩøÁî®Ê≠£Á°ÆÁöÑÁ±ªÂûã
                code: ({ node, className, children, ...props }: CodeProps) => {
                  // ÈÄöËøáÂà§Êñ≠ className ÊòØÂê¶ÂåÖÂê´ language- Êù•Âå∫ÂàÜË°åÂÜÖ‰ª£Á†ÅÂíå‰ª£Á†ÅÂùó
                  const isInline = !className || !className.includes('language-');

                  if (isInline) {
                    return (
                      <code className="inline-code" {...props}>
                        {children}
                      </code>
                    );
                  } else {
                    return (
                      <div className="code-block">
                        <code className={className} {...props}>
                          {children}
                        </code>
                      </div>
                    );
                  }
                },

                // Ë°®Ê†º
                table: ({ children }) => (
                  <div style={{ overflowX: 'auto', margin: '10px 0' }}>
                    <table style={{ borderCollapse: 'collapse', width: '100%' }}>
                      {children}
                    </table>
                  </div>
                ),

                // ÂºïÁî®
                blockquote: ({ children }) => (
                  <blockquote style={{
                    margin: '10px 0',
                    padding: '10px 15px',
                    borderLeft: '4px solid #4facfe',
                    background: 'rgba(79, 172, 254, 0.05)',
                    fontStyle: 'italic'
                  }}>
                    {children}
                  </blockquote>
                ),

                // Ê†áÈ¢ò
                h1: ({ children }) => <h1 style={{ fontSize: '1.8em', margin: '1em 0 0.5em' }}>{children}</h1>,
                h2: ({ children }) => <h2 style={{ fontSize: '1.5em', margin: '1.2em 0 0.5em' }}>{children}</h2>,
                h3: ({ children }) => <h3 style={{ fontSize: '1.3em', margin: '1em 0 0.5em' }}>{children}</h3>,
                h4: ({ children }) => <h4 style={{ fontSize: '1.1em', margin: '0.8em 0 0.3em', fontWeight: 600 }}>{children}</h4>,

                // ÂàóË°®
                ul: ({ children }) => <ul style={{ paddingLeft: '20px', margin: '8px 0' }}>{children}</ul>,
                ol: ({ children }) => <ol style={{ paddingLeft: '20px', margin: '8px 0' }}>{children}</ol>,
                li: ({ children }) => <li style={{ margin: '4px 0' }}>{children}</li>,

                // ÊÆµËêΩ
                p: ({ children }) => <p style={{ margin: '10px 0' }}>{children}</p>
              }}
            >
              {message.content}
            </ReactMarkdown>
          </div>
          <div className="message-time">{timeString}</div>
        </div>
      </div>
    </div>
  );
};

export default MessageBubble;
